import { useState } from 'react';
import './App.css';

function Button({ text, handle }) {
  return (
    <button onClick={() => handle(text)}>{text}</button>
  );
}

function NavBar({ list, handle }) {
  return (
    <div className='divBar'>
      {list.map((item) => (
        <Button key={item} text={item} handle={handle} />
      ))}
    </div>
  );
}

function App() {
  const [view, setView] = useState("둘러보기");
  const list = ["둘러보기", "등록하기", "게시판"];

  return (
    <div className="App">
      <header className="App-header">
        <NavBar list={list} handle={setView} />
      </header>
      <main>
        <h1>{view} 페이지</h1>
      </main>
    </div>
  );
}

export default App;
