import { useState } from 'react';
import './App.css';

function Button({ text, handle }) {
  return (
    <button className="Button" onClick={() => handle(text)}>{text}</button>
  );
}

function NavBar({ list, handle }) {
  return (
    <nav className='NavBar'>
      <h1 className='title'>농락</h1>
      <div className='buttonBar'>
        {list.map((item) => (
          <Button key={item} text={item} handle={handle} />
        ))}
      </div>
      <hr />
    </nav>
  );
}

function Main({view, list}) {
  return (
    <main>
      {view}
    </main>
  );
}

function 둘러보기() {
  return (
    <div>
      hello 
    </div>
  );
}

function App() {
  const [view, setView] = useState(<둘러보기 />);
  const list = ["둘러보기", "등록하기", "게시판", "김환성병신"];

  return (
    <div className="App">
      <header className="App-header">
        <NavBar list={list} handle={setView} />
      </header>
      <Main />
    </div>
  );
}

export default App;
