import { useState } from 'react';
import './App.css';

function Button({ text, handle }) {
  return (
    <button className="Button" onClick={() => handle(text)}>{text}</button>
  );
}

function User({handle}) {
  return (
    <div className={"User"}>
      <img className="login" src="login.png" onClick={() => handle("로그인")} />
      <Button text={"로그인"} handle={handle}/>
    </div>
  );
}

function NavBar({ list, handle }) {
  return (
    <nav className='NavBar'>
      <img className="icon" src='nonglock.png' />
      <h1 className='title'>농락</h1>
      <div className='buttonBar'>
        {Object.keys(list).map((key) => (
          <Button text={key} handle={handle} />
        ))}
      </div>
      <User handle={handle} />
      <hr />
    </nav>
  );
}

function Main({ view }) {
  return (
    <main className='Main'>
      {view}
    </main>
  );
}

function Catalog() {
  return (
    <section>
      0
    </section>
  );
}

function Regist() {
  return (
    <section>
      1
    </section>
  );
}

function Community() {
  return (
    <section>
      2
    </section>
  );
}

function Login() {
  return (
    <section>
      3
    </section>
  );
}

function App() {
  const viewList = {
    "둘러보기": <Catalog />,
    "등록하기": <Regist />,
    "소통마당": <Community />,
  };

  const [view, setView] = useState(viewList["둘러보기"]);

  const handleViewChange = (key) => {
    if (key=="로그인") {
      setView(<Login />)
    } else {
      setView(viewList[key]);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <NavBar list={viewList} handle={handleViewChange} />
      </header>
      <Main view={view} />
    </div>
  );
}

export default App;
