import { useState } from 'react';
import './App.css';

function Button({ text, handle }) {
  return (
    <button className="Button" onClick={() => handle(text)}>{text}</button>
  );
}

function NavBar({ list, handle }) {
  return (
    <nav className='NavBar'>
      <h1 className='title'>농락</h1>
      <div className='buttonBar'>
        {list.map((item) => (
          <Button key={item} text={item} handle={handle} />
        ))}
      </div>
      <hr />
    </nav>
  );
}

function Main({ view }) {
  return (
    <main>
      {view}
    </main>
  );
}

function 둘러보기() {
  return (
    <section>
      <h2>둘러보기</h2>
      <p>hello</p>
    </section>
  );
}

function Section2() {
  return (
    <section>
      <h2>등록하기</h2>
      <p>등록 내용을 여기에 표시합니다.</p>
    </section>
  );
}

function Section3() {
  return (
    <section>
      <h2>게시판</h2>
      <p>게시판 내용을 여기에 표시합니다.</p>
    </section>
  );
}

function App() {
  const [view, setView] = useState(<Section1 />);
  const list = ["둘러보기", "등록하기", "게시판", "김환성병신"];

  return (
    <div className="App">
      <header className="App-header">
        <NavBar list={list} handle={setView} />
      </header>
      <Main view={view} />
    </div>
  );
}

export default App;
